import {
  Button,
  Card,
  CardActions,
  CardContent,
  makeStyles,
  Menu,
  MenuItem,
  TextField,
  Typography,
} from "@material-ui/core"
import { useState } from "react"

const useStyles = makeStyles((theme) => ({
  feed: {
    maxWidth: 700,
    margin: "auto",
  },
  title: {
    fontSize: 14,
    marginTop: theme.spacing(2),
  },
  commentArea: {
    display: "block",
  },
  postComment: {
    marginTop: theme.spacing(2),
  },
}))

const Feed = (props) => {
  const classes = useStyles()
  const [commentsToggle, setCommentsToggle] = useState(false)

  const handleCommentsClick = (event) => {
    setCommentsToggle(!commentsToggle)
  }

  return (
    <Card className={classes.feed} variant="outlined" color="primary">
      <CardContent>
        <Typography
          className={classes.title}
          color="textSecondary"
          gutterBottom
        >
          Posted by: {props.poster}
        </Typography>
        <Typography component="h3" variant="h6">
          {props.post}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" onClick={handleCommentsClick}>
          Comments ({props.comments})
        </Button>
        <Button size="small">Likes ({props.likes})</Button>
      </CardActions>
      <CardContent>

      </CardContent>
      <CardActions className={classes.commentArea}>
        <TextField
          fullWidth
          label="Add a comment"
          name="comment"
          size="small"
          variant="outlined"
        />
        <Button
          variant="contained"
          color="primary"
          className={classes.postComment}
        >
          Add Comment
        </Button>
      </CardActions>
    </Card>
  )
}

export default Feed
