import { Card, Container, makeStyles, CardContent } from "@material-ui/core"
import Post from "../components/Post"
import CreatePost from "./CreatePost"
import { gql, useQuery } from "@apollo/client"

/**
 * Represents the query to fetch for the feed.
 */
export const GET_FEED_QUERY = gql`
  query {
    getFeed {
      id
      post
      user {
        id
        firstName
        lastName
      }
    }
  }
`

/**
 * Creates the styling of the feed page.
 */
const useStyles = makeStyles((theme) => ({
  container: {
    marginTop: theme.spacing(4),
  },
}))

const FeedPage = () => {
  const classes = useStyles()

  const { loading, error, data } = useQuery(GET_FEED_QUERY, {
    errorPolicy: "all",
    pollInterval: 100,
  })

  if (loading) {
    return <span>Loading....</span>
  }

  if (error) {
    return <span>Error!</span>
  }

  return (
    <Container className={classes.container} maxWidth="sm">
      <CreatePost />
      {data.getFeed.map((post) => {
        return (
          <Post
            poster={post.user.firstName + " " + post.user.lastName}
            post={post.post}
            comments={0}
            likes={0}
          />
        )
      })}
    </Container>
  )
}

export default FeedPage
