import {
  Button,
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  makeStyles,
  TextField,
  Typography,
} from "@material-ui/core"
import Comments from "./Comments"
import { useState } from "react"
import { gql, useMutation, useQuery } from "@apollo/client"
import ThumbUpIcon from "@material-ui/icons/ThumbUp"
import ChatBubbleIcon from "@material-ui/icons/ChatBubble"

const ADD_COMMENT = gql`
  mutation AddComment($comment: String!, $postId: Int!) {
    addComment(comment: $comment, postId: $postId) {
      id
      comment
      post {
        id
        post
      }
      user {
        id
        firstName
        lastName
      }
    }
  }
`

const GET_COMMENTS = gql`
  query GetComments($postId: Int!) {
    getComments(postId: $postId) {
      id
      comment
      post {
        id
      }
      user {
        id
        firstName
        lastName
      }
    }
  }
`

const useStyles = makeStyles((theme) => ({
  feed: {
    margin: "auto",
    marginTop: theme.spacing(4),
  },
  title: {
    fontSize: 14,
    marginTop: theme.spacing(2),
  },
  commentArea: {
    display: "block",
  },
  postComment: {
    marginTop: theme.spacing(2),
  },
  icon: {
    margin: theme.spacing(1),
  },
  card: {
    borderColor: "blue",
  },
}))

const Post = (props) => {
  const classes = useStyles()
  const [commentsToggle, setCommentsToggle] = useState(false)
  const [comment, setComment] = useState("")

  const [addComment, { error }] = useMutation(ADD_COMMENT, {
    onError({ error }) {},
  })

  const { loading, err, data } = useQuery(GET_COMMENTS, {
    errorPolicy: "all",
    pollInterval: 100,
    variables: {
      postId: parseInt(props.id),
    },
    fetchPolicy: "cache-and-network",
  })

  const handleCommentChange = (event) => {
    setComment(event.target.value)
  }

  const handleCommentsClick = (event) => {
    setCommentsToggle(!commentsToggle)
  }

  const handleAddComment = (event) => {
    addComment({ variables: { comment: comment, postId: parseInt(props.id) } })
    setComment("")
    setCommentsToggle(true)
  }
  if (error) {
    return <span>{JSON.stringify(error)}</span>
  }
  return (
    <Card className={classes.feed} variant="outlined" color="primary">
      <CardContent>
        <Typography
          className={classes.title}
          color="textSecondary"
          gutterBottom
        >
          Posted by: {props.poster}
        </Typography>
        <Typography component="h3" variant="h6">
          {props.post}
        </Typography>
      </CardContent>

      {commentsToggle && data ? (
        <Comments comments={data.getComments} postId={props.id}></Comments>
      ) : (
        ""
      )}
      <CardActionArea>
        <CardActions>
          <Button size="small" onClick={handleCommentsClick}>
            <ChatBubbleIcon className={classes.icon} color="primary" /> (
            {data ? data.getComments.length : "0"})
          </Button>
          <Button size="small">
            <ThumbUpIcon className={classes.icon} color="primary" />({0})
          </Button>
        </CardActions>
        <CardActions className={classes.commentArea}>
          <TextField
            fullWidth
            value={comment}
            label="Add a comment"
            name="comment"
            size="small"
            variant="outlined"
            onChange={handleCommentChange}
          />
          <Button
            variant="contained"
            color="primary"
            className={classes.postComment}
            onClick={handleAddComment}
          >
            Add Comment
          </Button>
        </CardActions>
      </CardActionArea>
    </Card>
  )
}

export default Post
