import {
  Button,
  Card,
  CardActions,
  CardContent,
  makeStyles,
  TextField,
  Typography,
} from "@material-ui/core"

const useStyles = makeStyles((theme) => ({
  feed: {
    maxWidth: 700,
    margin: "auto",
  },
  title: {
    fontSize: 14,
  },
  commentArea: {
    display: "block",
  },
  postComment: {
    marginTop: theme.spacing(2),
  },
}))

const Feed = (props) => {
  const classes = useStyles()
  return (
    <Card className={classes.feed} variant="outlined" color="primary">
      <CardContent>
        <Typography component="h3" variant="h5">
          {props.post}
        </Typography>
        <Typography
          className={classes.title}
          color="textSecondary"
          gutterBottom
        >
          Posted by: Adam Rodrigues
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small">Comments ({props.comments})</Button>
        <Button size="small">Likes ({props.likes})</Button>
      </CardActions>
      <CardActions className={classes.commentArea}>
        <TextField
          fullWidth
          label="Add a comment"
          name="comment"
          size="small"
          variant="outlined"
        />
        <Button
          variant="contained"
          color="primary"
          className={classes.postComment}
        >
          Add Comment
        </Button>
      </CardActions>
    </Card>
  )
}

export default Feed
