import {
  Button,
  Card,
  CardActionArea,
  CardActions,
  CardContent,
  makeStyles,
  TextField,
  Typography,
} from "@material-ui/core"
import Comments from "./Comments"
import { useState } from "react"
import { gql, useMutation, useQuery } from "@apollo/client"

const GET_COMMENTS = gql`
  query GetComments($postId: Int!) {
    getComments(postId: $postId) {
      id
      comment
      post {
        id
      }
      user {
        id
        firstName
        lastName
      }
    }
  }
`

const useStyles = makeStyles((theme) => ({
  feed: {
    margin: "auto",
    marginTop: theme.spacing(4),
  },
  title: {
    fontSize: 14,
    marginTop: theme.spacing(2),
  },
  commentArea: {
    display: "block",
  },
  postComment: {
    marginTop: theme.spacing(2),
  },
}))

const Post = (props) => {
  const classes = useStyles()
  const [commentsToggle, setCommentsToggle] = useState(false)
  const [comment, setComment] = useState("")

  const handleCommentChange = (event) => {
    setComment(event.target.value)
  }

  const handleCommentsClick = (event) => {
    setCommentsToggle(!commentsToggle)
  }

  const handleAddComment = (event) => {
    addComment({ variables: { comment: comment, postId: props.id } })
  }

  return (
    <Card className={classes.feed} variant="outlined" color="primary">
      <CardContent>
        <Typography
          className={classes.title}
          color="textSecondary"
          gutterBottom
        >
          Posted by: {props.poster}
        </Typography>
        <Typography component="h3" variant="h6">
          {props.post}
        </Typography>
      </CardContent>

      {commentsToggle && data ? <Comments postId={props.id} /> : ""}
      <CardActionArea>
        <CardActions>
          <Button size="small" onClick={handleCommentsClick}>
            Comments ({props.comments})
          </Button>
          <Button size="small">Likes ({props.likes})</Button>
        </CardActions>
        <CardActions className={classes.commentArea}>
          <TextField
            fullWidth
            label="Add a comment"
            name="comment"
            size="small"
            variant="outlined"
            onChange={handleCommentChange}
          />
          <Button
            variant="contained"
            color="primary"
            className={classes.postComment}
            onClick={handleAddComment}
          >
            Add Comment
          </Button>
        </CardActions>
      </CardActionArea>
    </Card>
  )
}

export default Post
