  return (
    <Container className={classes.container} maxWidth="sm">
      <CreatePost />
      {getFeed().map((post) => {
        return (
          <Post
            poster={post.poster}
            post={post.post}
            comments={post.comments}
            likes={post.likes}
          />
        )
      })}
    </Container>
  )
}

export default FeedPage
